package com.video.player.fullvideoplayer.hdplayer;

import android.app.Application;
import android.content.Context;
import androidx.multidex.MultiDex;

import com.video.player.fullvideoplayer.hdplayer.kxUtil.kxUtils;

public class KxApp extends Application {
    private static KxApp ourInstance = new KxApp();

    public static KxApp getInstance() {
        return ourInstance;
    }

    private String base64PublicKey = "";

    @Override
    public void onCreate() {
        super.onCreate();
        ourInstance = this;

        kxUtils.init(this);

    }
    @Override
    protected void attachBaseContext(Context base) {
        super.attachBaseContext(base);
        MultiDex.install(this);
    }

}
